/*Table structure for table `subcompany` */

DROP TABLE IF EXISTS `subcompany`;

CREATE TABLE `subcompany` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `name` varchar(30) COLLATE utf8mb4_unicode_ci NOT NULL COMMENT '子公司名称',
  `picture` varchar(200) COLLATE utf8mb4_unicode_ci DEFAULT NULL COMMENT '图片',
  `province` varchar(10) COLLATE utf8mb4_unicode_ci DEFAULT NULL COMMENT '所在省份',
  `summary` varchar(200) COLLATE utf8mb4_unicode_ci DEFAULT NULL COMMENT '简介',
  `detail` text COLLATE utf8mb4_unicode_ci COMMENT '详情',
  `sort` int(11) DEFAULT '0' COMMENT '排序，值越大越靠前',
  PRIMARY KEY (`id`)
) ENGINE=MyISAM AUTO_INCREMENT=31 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;

